(196, '', '', '', '', '', 'If you\'re feeling frustrated for some reason, _ whatever you\'re doing and _ _ later.', 'stop come back', '2024-01-11 18:39:34', '', '', '', 0),
(197, 'come back', '(phrasal verb)', '', 'to return to a place / If a style or a fashion comes back, it becomes popular again after being unpopular for a period of time / to remember something you had previously forgotten', 'I\'ll come back and pick you up in half an hour.\r\nPadded shoulders are coming back, apparently.\r\nAs soon as she entered the school, childhood memories came rushing back.', '', '', '2024-01-11 18:42:18', 'view/uploads/image/18_42_1_12_1_2024_image.png', '', '', 0),
(198, 'configure', '(verb)', '/kənˈfɪɡjər/', 'to arrange something in a particular way, especially computer equipment; to make equipment or software work in the way that the user prefers', '', '', '', '2024-01-11 18:46:34', 'view/uploads/image/34_46_1_12_1_2024_image.png', 'view/uploads/audio/34_46_1_12_1_2024_audio.mp3', '', 0),
(199, 'use it as the name says to', '', '', 'sử dụng nó như tên gọi để', '', '', '', '2024-01-11 18:51:54', 'view/uploads/image/02_57_1_12_1_2024_image.png', '', '', 0),
(200, 'by far', '', '', 'hơn hẳn / bỏ xa / xa hơn', 'He is by far the best student in the class', '', '', '2024-01-11 19:04:00', 'view/uploads/image/00_04_2_12_1_2024_image.png', '', '', 0),
(201, 'the best N by far that exists', '', '', 'N tốt nhất hiện nay', '', '', '', '2024-01-11 19:13:19', '', '', '', 0),
(202, 'alternative', '(noun)', '/ɔːlˈtɜːrnətɪv/', 'a thing that you can choose to do or have out of two or more possibilities', 'There are alternatives if you don\'t like VS Code for some reason. So you can use Adam or you can use something like Brackets.\r\nThe car is too expensive so we\'re trying to find a cheaper alternative.\r\nThere is a vegetarian alternative on the menu every day.\r\nWe had no alternative but to fire Gibson.', '', '', '2024-01-11 19:24:42', 'view/uploads/image/30_07_15_21_1_2024_image.png', 'view/uploads/audio/42_24_2_12_1_2024_audio.mp3', '', 0),
(203, 'affiliated', '(adjective)', '/əˈfɪlieɪtɪd/', 'closely connected to or controlled by a group or an organization', 'The doctors are affiliated with the Relief Fund.\r\nI\'m not in any way affiliated with the theme.', '', '', '2024-01-11 19:59:01', 'view/uploads/image/14_08_23_21_1_2024_image.png', 'view/uploads/audio/01_59_2_12_1_2024_audio.mp3', '', 0),
(204, 'advertisement', '(noun)', '/ˌædvərˈtaɪzmənt/', 'a notice, picture or film telling people about a product, job or service.\r\n', 'Television and radio refused to carry advertisements for the album.', '', '', '2024-01-11 20:03:55', 'view/uploads/image/47_56_5_21_1_2024_image.png', 'view/uploads/audio/55_03_3_12_1_2024_audio.mp3', '', 0),
(205, 'cursor', '(noun)', '/ˈkɜːrsər/', 'a small mark on a computer screen that can be moved and that shows the position on the screen where, for example, text will be added', '', '', '', '2024-01-11 20:11:32', 'view/uploads/image/37_47_22_21_1_2024_image.png', 'view/uploads/audio/32_11_3_12_1_2024_audio.mp3', '', 0),
(206, 'modifier', '(noun)', '/ˈmɑːdɪfaɪər/', 'bổ ngữ / a word or group of words that describes a noun phrase or limits its meaning in some way / công cụ sửa đổi', 'multi cursor modifier', '', '', '2024-01-11 20:15:54', 'view/uploads/image/54_15_3_12_1_2024_image.png', 'view/uploads/audio/54_15_3_12_1_2024_audio.mp3', '', 0),
